/* Annotation descriptions for use in tooltips */
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to activity class (e.g., DNA damage, anti-inflammatory) annotations provided by the DrugMatrix Database.' WHERE annoclassname = 'ACTIVITY_CLASS';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to adverse effect annotations curated from the PDR, drug package inserts, or primary literature. Effects are score based on severity (SSS for severe to S for moderate/minimal severity) and frequency of occurrence (1-most frequent, 2-less common, 3-rare).' WHERE annoclassname = 'ADVERSE_EFFECT';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to disease (e.g., prostate cancer) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature. Some associations in this set have been inferred based on bridging linkages through genes.' WHERE annoclassname = 'CTD_CHEMICALS_DISEASES';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to gene (e.g., ESR1) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature.' WHERE annoclassname = 'CTD_CHEMICALS_GENES';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to gene ontology cellular component (e.g., mitochondria) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature. Some associations in this set have been inferred based on bridging linkages through genes.' WHERE annoclassname = 'CTD_CHEMICALS_GOENRICH_CELLCOMP';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to gene ontology molecular function (e.g., catalysis) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature. Some associations in this set have been inferred based on bridging linkages through genes.' WHERE annoclassname = 'CTD_CHEMICALS_GOENRICH_MOLFUNCT';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to molecular pathway (e.g., P53 pathway) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature.' WHERE annoclassname = 'CTD_CHEMICALS_PATHWAYS';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to gene ontology biological processes (e.g., xenobiotic metabolism) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature. Some associations in this set have been inferred based on bridging linkages through genes. Only includes terms found in the GO Fat subset.' WHERE annoclassname = 'CTD_GOFAT_BIOPROCESS';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to gene ontology biological processes (e.g., xenobiotic metabolism) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature. Some associations in this set have been inferred based on bridging linkages through genes. Only includes terms found in the GO Slim subset.' WHERE annoclassname = 'CTD_GOSLIM_BIOPROCESS';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to ATC (anatomic therapeutic category) code annotations maintained by WHO and extracted from DrugBank.' WHERE annoclassname = 'DRUGBANK_ATC';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to ATC (anatomic therapeutic category) code annotations maintained by WHO and extracted from DrugBank.' WHERE annoclassname = 'DRUGBANK_ATC_CODE';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to blood carrier protein (e.g., ALB) annotations provided by DrugBank.' WHERE annoclassname = 'DRUGBANK_CARRIERS';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to metabolic enzyme (e.g., CYP1A1) annotations provided by DrugBank.' WHERE annoclassname = 'DRUGBANK_ENZYMES';
UPDATE public.annotation_class SET annodesc = 'Chemical to molecular target (e.g., ESR1) annotations derived from DrugBank.' WHERE annoclassname = 'DRUGBANK_TARGETS';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to transporter (e.g., ABCC1) annotations provided the DrugBank.' WHERE annoclassname = 'DRUGBANK_TRANSPORTERS';
UPDATE public.annotation_class SET annodesc = 'Chemical to in vitro biological activity annotations from the Tox21 assays.' WHERE annoclassname = 'HTS_ACTIVE';
UPDATE public.annotation_class SET annodesc = 'Chemical to in vitro biological activity annotations from the Tox21 assays.' WHERE annoclassname = 'HTS_STRONGACTIVE';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to clinical indication annotations provided by the DrugMatrix Database.' WHERE annoclassname = 'INDICATION';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to known toxicological effects (e.g., liver toxicity) provided by the DrugMatrix Database.' WHERE annoclassname = 'KNOWN_TOXICITY';
UPDATE public.annotation_class SET annodesc = 'Chemical to SAR-based predicted toxicological effects provided by Leadscope SAR models.' WHERE annoclassname = 'LEADSCOPE_TOXICITY';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to mechanistic (e.g., androgen receptor antagonist) annotations that are the least specific of all three levels provided by the DrugMatrix Database.' WHERE annoclassname = 'MECH_LEVEL_1';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to mechanistic (e.g., androgen receptor antagonist) annotations that are more specific that level 1 and less specific than level 3.' WHERE annoclassname = 'MECH_LEVEL_2';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to mechanistic (e.g., androgen receptor antagonist) annotations that are more specific than level 2.' WHERE annoclassname = 'MECH_LEVEL_3';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to mechanism (e.g. lipid metabolism, block neuronal transmission) annotations provided by the DrugMatrix Database.' WHERE annoclassname = 'MECHANISM';
UPDATE public.annotation_class SET annodesc = 'Chemical to MeSH Chemical and Drug [D] annotations; specifically, the terminal branch MeSH terms in the "D" subset provided by Pubchem.' WHERE annoclassname = 'MESH';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to mode class (e.g., enzyme inhibitor, receptor agonist) annotations provided by the DrugMatrix Database.' WHERE annoclassname = 'MODE_CLASS';
UPDATE public.annotation_class SET annodesc = 'Chemical to SAR-based predicted toxicological effects provided by Multicase SAR models.' WHERE annoclassname = 'MULTICASE_TOX_PREDICTION';
UPDATE public.annotation_class SET annodesc = 'Chemical to pharmacological action terms derived from the USNLM. Provided by the DrugMatrix Database.' WHERE annoclassname = 'PHARMACTIONLIST';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to product class (i.e., target system, e.g., endocrine, CNS) annotations derived from the DrugMatrix Database.' WHERE annoclassname = 'PRODUCT_CLASS';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to structural activity class (i.e., molecular target associated with therapeutic effects) annotations derived from the DrugMatrix Database.' WHERE annoclassname = 'STRUCTURE_ACTIVITY';
UPDATE public.annotation_class SET annodesc = 'Chemical to drug/therapeutic use category (e.g., infectious disease). TA_LEVEL_1 is the highest (i.e., most abstract) drug use category. Provided by DrugMatrix.' WHERE annoclassname = 'TA_LEVEL_1';
UPDATE public.annotation_class SET annodesc = 'Chemical to drug use/therapeutic category that is more specific than TA_LEVEL_1 (e.g., solid tumors). Provided by the DrugMatrix Database.' WHERE annoclassname = 'TA_LEVEL_2';
UPDATE public.annotation_class SET annodesc = 'Chemical to drug use/therapeutic category that is more specific than TA_LEVEL_2 (e.g., prostate cancer). Provided by the DrugMatrix Database.' WHERE annoclassname = 'TA_LEVEL_3';
UPDATE public.annotation_class SET annodesc = 'Chemical/drug to therapeutic class (e.g., antidiabetic agents) annotations derived from the DrugMatrix database.' WHERE annoclassname = 'THERAPEUTIC_CLASS';
UPDATE public.annotation_class SET annodesc = 'Chemical to target tissue toxicity annotations derived from the DrugMatrix Database.' WHERE annoclassname = 'TISSUE_TOXICITY';
UPDATE public.annotation_class SET annodesc = 'Chemical to in vitro biological activity annotations from the EPA ToxCast data.' WHERE annoclassname = 'TOXCAST_ACTIVE';
UPDATE public.annotation_class SET annodesc = 'Chemical to molecular target annotations. The annotations in this set associate chemicals with their known molecular targets/molecular initiators (e.g., receptors) that have been curated in the Toxin and Toxin Target Database.' WHERE annoclassname = 'TOXINS_TARGETS';
UPDATE public.annotation_class SET annodesc = 'Chemical to substructure/chemotype annotations. A detailed description of the ToxPrint chemotypes can be found here (https://toxprint.org/).' WHERE annoclassname = 'TOXPRINT_STRUCTURE';
UPDATE public.annotation_class SET annodesc = 'Chemical to in vivo toxicological effect annotations. The annotations in the set associate chemicals with their toxicological effects in guideline toxicity studies.' WHERE annoclassname = 'TOXREFDB';

