/* Annotation descriptions for use in tooltips */
UPDATE public.annotation_class SET annodesc = 'Chemical/Drug to activity class (e.g. DNA damager, anti-inflammatory) annotation provided by the DrugMatrix Database.' WHERE annoclassname = 'ACTIVITY_CLASS';
UPDATE public.annotation_class SET annodesc = 'Chemical/Drug to Adverse effect annotations curated from the PDR, drug package inserts or primary literature. Effects are score based on severity (SSS for severe to S for moderate/minimal severity) and frequency of occurance (1- most frequent, 2-less common, 3-rare).' WHERE annoclassname = 'ADVERSE_EFFECT';
UPDATE public.annotation_class SET annodesc = 'Chemical/Drug to disease (e.g., prostate cancer) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature. Some associations in this set have been inferred based on bridging linkages through genes.' WHERE annoclassname = 'CTD_CHEMICALS_DISEASES';
UPDATE public.annotation_class SET annodesc = 'Chemical/Drug to gene (e.g., ESR1) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature.' WHERE annoclassname = 'CTD_CHEMICALS_GENES';
UPDATE public.annotation_class SET annodesc = 'Chemical/Drug to Gene Ontology Cellular Component (e.g., Mitochondria) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature. Some associations in this set have been inferred based on bridging linkages through genes.' WHERE annoclassname = 'CTD_CHEMICALS_GOENRICH_CELLCOMP';
UPDATE public.annotation_class SET annodesc = 'Chemical/Drug to Gene Ontology Molecular Function (e.g., catalysis) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature. Some associations in this set have been inferred based on bridging linkages through genes.' WHERE annoclassname = 'CTD_CHEMICALS_GOENRICH_MOLFUNCT';
UPDATE public.annotation_class SET annodesc = 'Chemical/Drug to molecular pathway (e.g., P53 pathway) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature.' WHERE annoclassname = 'CTD_CHEMICALS_PATHWAYS';
UPDATE public.annotation_class SET annodesc = 'Chemical/Drug to Gene Ontology Biological Processes (e.g., Xenobiotic Metabolism) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature. Some associations in this set have been inferred based on bridging linkages through genes. Only includes terms found in the GO Fat subset.' WHERE annoclassname = 'CTD_GOFAT_BIOPROCESS';
UPDATE public.annotation_class SET annodesc = 'Chemical/Drug to Gene Ontology Biological Processes (e.g., Xenobiotic Metabolism) associations that have been curated in the Comparative Toxicogenomics Database through review of the literature. Some associations in this set have been inferred based on bridging linkages through genes. Only includes terms found in the GO Slim subset.' WHERE annoclassname = 'CTD_GOSLIM_BIOPROCESS';
